import React, { Component } from 'react'
import { Fragment } from 'react';

export default class EditMovie extends Component {

    state = {
        movie: {

        },
        isLoaded: false,
        error: null,
    }

    render() {
        let { movie, isLoaded, error } = this.state;
        return (
            <Fragment>
                <h2>Add/Edit Movie</h2>
                <form method='post'>
                    <div className="mb-3">
                        <label for="title" class="form-label">
                            Title
                        </label>
                        <input type="text" className="form-control" id="title" name="title" value={movie.title} />
                    </div>

                    <div className="mb-3">
                        <label for="release_date" class="form-label">
                            Release Date
                        </label>
                        <input type="text" className="form-control" id="release_date" name="release_date" value={movie.release_date} />
                    </div>

                    <div className="mb-3">
                        <label for="title" class="form-label">
                            Title
                        </label>
                        <input type="text" className="form-control" id="title" name="title" value={movie.title} />
                    </div>



                    <button type="submit" class="btn btn-primary">Submit</button>
                </form>
            </Fragment>
        );
    }
}
